total = msg.payload[0].size  / 1024
units = "M"
if (total > 1024) {
    units = "G"
    total = Math.round(total / 1024* 1 )/1;}
else {

    total = Math.round(total * 1 )/1;
}

used = msg.payload[0].used / 1024
uunits = "M"
if (used > 1024) {
    uunits = "G"
    used = Math.round(used / 1024* 10 )/10;}
else {

    used = Math.round(used * 10 )/10;
}

texto = "Disk: " + total + units + " " + used + uunits + " " + Math.round(msg.payload[0].capacity * 100) + "%"
texto = texto.replace(".", ",");
msg.payload = {"size": 1, "x": 6, "y": 53, "text": texto};
return msg;
